import React from 'react';

const users = [
  { id: 1, name: 'John Doe', avatar: '/avatars/john.png', online: true },
  { id: 2, name: 'Jane Smith', avatar: '/avatars/jane.png', online: true },
];

function Sidebar() {
  return (
    <div className="w-full md:w-64 bg-[#2b2345] text-white p-4 space-y-4 h-screen overflow-y-auto">
      <input
        type="text"
        placeholder="Search..."
        className="w-full p-2 rounded-md bg-[#3b2e5a] text-white placeholder-gray-300 outline-none"
      />
      <ul className="space-y-3 mt-4">
        {users.map(user => (
          <li
            key={user.id}
            className="flex items-center gap-4 px-4 py-2 rounded-lg hover:bg-[#3a2d5c] cursor-pointer transform hover:scale-[1.01] transition"
          >
            <img src={user.avatar} alt="Avatar" className="w-10 h-10 rounded-full" />
            <div className="flex-1">
              <h3 className="text-white font-medium">{user.name}</h3>
              <p className="text-sm text-green-400 flex items-center gap-2">
                <span className="w-2 h-2 rounded-full bg-green-400 animate-pulse"></span>
                Online
              </p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Sidebar;
