import React from 'react';

const Sidebar = ({ onSelect, onShowProfile }) => {
  const users = [
    { id: 'u1', name: 'Alice', avatar: 'https://i.pravatar.cc/150?img=5' },
    { id: 'u2', name: 'Bob', avatar: 'https://i.pravatar.cc/150?img=6' },
  ];

  const groups = [
    { id: 'g1', name: 'React Developers' },
    { id: 'g2', name: 'Open Source Contributors' },
  ];

  return (
    <div className="w-64 bg-white shadow-lg overflow-y-auto">
      <div className="p-4 border-b">
        <h2 className="text-lg font-semibold text-gray-700">Chats</h2>
      </div>

      {/* Users */}
      <div>
        <h3 className="px-4 py-2 text-sm font-bold text-gray-500 uppercase">Users</h3>
        {users.map(user => (
          <div
            key={user.id}
            className="flex items-center px-4 py-2 cursor-pointer hover:bg-gray-100"
            onClick={() =>
              onSelect({ type: 'user', data: user })
            }
          >
            <img src={user.avatar} alt={user.name} className="w-8 h-8 rounded-full mr-3" />
            <span className="text-sm font-medium text-gray-800">{user.name}</span>
          </div>
        ))}
      </div>

      {/* Groups */}
      <div>
        <h3 className="px-4 py-2 text-sm font-bold text-gray-500 uppercase">Groups</h3>
        {groups.map(group => (
          <div
            key={group.id}
            className="flex items-center px-4 py-2 cursor-pointer hover:bg-gray-100"
            onClick={() =>
              onSelect({ type: 'group', data: group })
            }
          >
            <span className="text-sm font-medium text-gray-800"># {group.name}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Sidebar;
