import React, { useState, useEffect, useRef } from 'react';

const GroupChat = ({ group, onBack }) => {
  // Example initial group messages (simulate fetching from backend)
  const [messages, setMessages] = useState([
    { from: 'Alice', text: `Welcome to the ${group.name} group!`, timestamp: Date.now() - 60000 },
    { from: 'me', text: 'Hi everyone!', timestamp: Date.now() - 30000 },
  ]);
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef(null);

  // Scroll to bottom on new message
  useEffect(() => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  const handleSend = () => {
    if (!newMessage.trim()) return;
    const messageObj = {
      from: 'me',
      text: newMessage.trim(),
      timestamp: Date.now(),
    };
    setMessages(prev => [...prev, messageObj]);
    setNewMessage('');
  };

  return (
    <div className="flex flex-col flex-1 bg-white rounded-lg shadow-md p-4">
      <header className="flex items-center justify-between mb-4 border-b pb-2">
        <button
          onClick={onBack}
          className="text-indigo-600 hover:underline"
        >
          ‚Üê Back
        </button>
        <h2 className="text-xl font-semibold">{group.name}</h2>
      </header>

      <div className="mb-2 text-sm text-gray-600">
        Members: {group.members.join(', ')}
      </div>

      <div
        className="flex flex-col overflow-y-auto flex-grow mb-4"
        style={{ maxHeight: 'calc(100vh - 250px)', justifyContent: 'flex-end' }}
      >
        {messages.length === 0 && (
          <p className="text-gray-400 text-center mt-20">No messages yet.</p>
        )}

        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`mb-3 max-w-xs p-2 rounded ${
              msg.from === 'me' ? 'bg-indigo-200 self-end' : 'bg-gray-200 self-start'
            }`}
          >
            <p className="font-medium">{msg.from !== 'me' ? msg.from : 'You'}</p>
            <p>{msg.text}</p>
            <small className="text-gray-500 text-xs">
              {new Date(msg.timestamp).toLocaleTimeString()}
            </small>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="flex gap-2">
        <input
          type="text"
          placeholder="Type a message..."
          value={newMessage}
          onChange={e => setNewMessage(e.target.value)}
          className="flex-grow px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-indigo-500"
          onKeyDown={e => {
            if (e.key === 'Enter') handleSend();
          }}
        />
        <button
          onClick={handleSend}
          className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default GroupChat;
