import React, { useState, useRef, useEffect } from 'react';
import { ArrowLeft, Mic, Send, X } from 'lucide-react';
import EmojiPicker from 'emoji-picker-react';

const GroupChat = ({ group, messages, onSendMessage, onBack }) => {
  const [inputValue, setInputValue] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [recording, setRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Add emoji to input
  const onEmojiClick = (emojiData) => {
    setInputValue((prev) => prev + emojiData.emoji);
  };

  // Handle file input
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    onSendMessage(group.id, {
      from: 'me',
      type: 'file',
      file,
      text: file.name,
      timestamp: new Date().toISOString(),
    });
  };

  // Send message handler
  const handleSend = () => {
    if (!inputValue.trim() && !audioBlob) return;

    if (audioBlob) {
      const audioURL = URL.createObjectURL(audioBlob);
      onSendMessage(group.id, {
        from: 'me',
        type: 'audio',
        audioURL,
        audioBlob,
        timestamp: new Date().toISOString(),
      });
      setAudioBlob(null);
      setRecording(false);
      setInputValue('');
      return;
    }

    onSendMessage(group.id, {
      from: 'me',
      type: 'text',
      text: inputValue.trim(),
      timestamp: new Date().toISOString(),
    });
    setInputValue('');
  };

  // Handle enter key send
  const handleKeyDown = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  // Handle recording start/stop
  const toggleRecording = () => {
    if (recording) {
      mediaRecorder.stop();
    } else {
      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        alert('Audio recording is not supported in this browser.');
        return;
      }

      navigator.mediaDevices.getUserMedia({ audio: true }).then((stream) => {
        const recorder = new MediaRecorder(stream);
        setMediaRecorder(recorder);
        recorder.start();
        setRecording(true);

        const chunks = [];
        recorder.ondataavailable = (e) => chunks.push(e.data);
        recorder.onstop = () => {
          const blob = new Blob(chunks, { type: 'audio/webm' });
          setAudioBlob(blob);
          setRecording(false);
        };
      });
    }
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-tl-3xl shadow-lg overflow-hidden">
      {/* Header */}
      <div className="flex items-center px-4 py-3 bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow">
        <button onClick={onBack} className="mr-4 p-2 hover:bg-purple-700 rounded-full">
          <ArrowLeft size={20} />
        </button>
        <div>
          <h2 className="text-lg font-bold">{group.name}</h2>
          <p className="text-sm">{group.members.length} members</p>
        </div>
      </div>

      {/* Message List */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3 bg-gray-100">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`max-w-xs px-4 py-2 rounded-lg shadow break-words ${
              msg.from === 'me'
                ? 'bg-blue-500 text-white self-end ml-auto'
                : 'bg-gray-300 text-black self-start mr-auto'
            }`}
          >
            {msg.type === 'text' && <p className="text-sm">{msg.text}</p>}

            {msg.type === 'file' && (
              <a
                href={URL.createObjectURL(msg.file)}
                target="_blank"
                rel="noreferrer"
                className="underline text-blue-800"
              >
                {msg.text}
              </a>
            )}

            {msg.type === 'audio' && (
              <audio controls src={msg.audioURL} className="w-full" />
            )}

            <span className="block text-[10px] mt-1 text-right opacity-70">
              {new Date(msg.timestamp).toLocaleTimeString([], {
                hour: '2-digit',
                minute: '2-digit',
              })}
            </span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Input & Controls */}
      <div className="flex items-center p-3 bg-white border-t">
        <button
          onClick={() => setShowEmojiPicker((v) => !v)}
          className="mr-2 p-2 hover:bg-gray-200 rounded-full"
          title="Toggle Emoji Picker"
        >
          😊
        </button>

        <input
          type="file"
          onChange={handleFileChange}
          className="hidden"
          id="file-upload"
        />
        <label
          htmlFor="file-upload"
          className="mr-2 cursor-pointer p-2 hover:bg-gray-200 rounded-full"
          title="Send File"
        >
          📎
        </label>

        <input
          type="text"
          className="flex-1 p-2 border rounded-full mr-2 focus:outline-none focus:ring-2 focus:ring-purple-400"
          placeholder="Type a message..."
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={handleKeyDown}
          disabled={recording}
        />

        {recording ? (
          <>
            <button
              onClick={() => {
                mediaRecorder?.stop();
                setRecording(false);
              }}
              className="mr-2 p-2 bg-red-500 text-white rounded-full"
              title="Stop Recording"
            >
              ✂️
            </button>
            <button
              onClick={handleSend}
              className="p-2 bg-green-600 text-white rounded-full"
              title="Send Recording"
            >
              <Send size={20} />
            </button>
          </>
        ) : (
          <>
            <button
              onClick={toggleRecording}
              className={`mr-2 p-2 rounded-full hover:bg-gray-200 ${
                recording ? 'bg-red-500 text-white' : ''
              }`}
              title="Record Audio"
            >
              <Mic size={20} />
            </button>
            <button
              onClick={handleSend}
              className="p-2 bg-purple-600 text-white rounded-full hover:bg-purple-700"
              title="Send Message"
            >
              <Send size={20} />
            </button>
          </>
        )}
      </div>

      {/* Emoji Picker */}
      {showEmojiPicker && (
        <div className="absolute bottom-16 left-4 shadow-lg rounded-lg">
          <EmojiPicker onEmojiClick={onEmojiClick} />
          <button
            className="absolute top-1 right-1 text-gray-600 hover:text-gray-900"
            onClick={() => setShowEmojiPicker(false)}
            title="Close Emoji Picker"
          >
            <X size={16} />
          </button>
        </div>
      )}
    </div>
  );
};

export default GroupChat;
