import React, { useState, useRef, useEffect } from 'react';
import { ArrowLeft, Mic, Paperclip, Smile } from 'lucide-react';
import EmojiPicker from 'emoji-picker-react';

const GroupChat = ({ group, messages, onSendMessage, onBack }) => {
  const [inputValue, setInputValue] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [attachment, setAttachment] = useState(null);
  const [recording, setRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = () => {
    if (inputValue.trim() || attachment) {
      onSendMessage({
        text: inputValue.trim(),
        file: attachment,
        type: attachment?.type?.startsWith('audio') ? 'audio' : attachment ? 'file' : 'text'
      });
      setInputValue('');
      setAttachment(null);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  const handleEmojiClick = (emojiData) => {
    setInputValue((prev) => prev + emojiData.emoji);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) setAttachment(file);
  };

  const handleStartRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const audioChunks = [];

      recorder.ondataavailable = (e) => audioChunks.push(e.data);
      recorder.onstop = () => {
        const audioBlob = new Blob(audioChunks, { type: 'audio/webm' });
        const audioFile = new File([audioBlob], 'recording.webm', { type: 'audio/webm' });
        setAttachment(audioFile);
      };

      recorder.start();
      setMediaRecorder(recorder);
      setRecording(true);
    } catch (error) {
      console.error('Mic access denied:', error);
    }
  };

  const handleStopRecording = () => {
    mediaRecorder?.stop();
    setRecording(false);
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-tl-3xl shadow-lg overflow-hidden">
      {/* Header */}
      <div className="flex items-center px-4 py-3 bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow">
        <button onClick={onBack} className="mr-4 p-2 hover:bg-purple-700 rounded-full">
          <ArrowLeft size={20} />
        </button>
        <div>
          <h2 className="text-lg font-bold">{group.name}</h2>
          <p className="text-sm">{group.members.length} members</p>
        </div>
      </div>

      {/* Message List */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3 bg-gray-100">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`max-w-xs px-4 py-2 rounded-lg shadow ${
              msg.from === 'me'
                ? 'bg-blue-500 text-white self-end ml-auto'
                : 'bg-gray-300 text-black self-start mr-auto'
            }`}
          >
            {msg.type === 'audio' ? (
              <audio controls src={URL.createObjectURL(msg.file)} />
            ) : msg.file ? (
              <a href={URL.createObjectURL(msg.file)} download className="text-white underline">
                {msg.file.name}
              </a>
            ) : (
              <p className="text-sm">{msg.text}</p>
            )}
            <span className="block text-[10px] mt-1 text-right opacity-70">
              {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
            </span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div className="flex flex-col p-3 bg-white border-t relative">
        {showEmojiPicker && (
          <div className="absolute bottom-16 left-3 z-10">
            <EmojiPicker onEmojiClick={handleEmojiClick} height={300} width={250} />
          </div>
        )}

        {attachment && (
          <div className="mb-2 text-sm text-gray-600">
            {attachment.type.startsWith('audio') ? 'Audio recorded' : attachment.name}
          </div>
        )}

        <div className="flex items-center space-x-2">
          <button onClick={() => setShowEmojiPicker((prev) => !prev)} className="p-2 text-gray-500">
            <Smile size={20} />
          </button>

          <label className="p-2 cursor-pointer text-gray-500">
            <Paperclip size={20} />
            <input type="file" className="hidden" onChange={handleFileChange} />
          </label>

          <input
            type="text"
            className="flex-1 p-2 border rounded-full focus:outline-none focus:ring-2 focus:ring-purple-400"
            placeholder="Type a message..."
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={handleKeyDown}
          />

          <button
            onClick={handleSend}
            className="px-4 py-2 bg-purple-600 text-white rounded-full hover:bg-purple-700"
          >
            Send
          </button>

          <button
            onClick={recording ? handleStopRecording : handleStartRecording}
            className={`p-2 rounded-full ${recording ? 'bg-red-500' : 'bg-green-500'} text-white`}
          >
            <Mic size={20} />
          </button>
        </div>
      </div>
    </div>
  );
};

export default GroupChat;
