import React, { useState, useRef, useEffect } from 'react';
import { ArrowLeft, Smile, Paperclip } from 'lucide-react';
import EmojiPicker from 'emoji-picker-react';

const GroupChat = ({ group, messages, onSendMessage, onBack }) => {
  const [inputValue, setInputValue] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);

  const handleSend = () => {
    if (!inputValue.trim() && !selectedFile) return;

    const message = {
      text: inputValue.trim(),
      from: 'me',
      timestamp: new Date().toISOString(),
    };

    if (selectedFile) {
      message.fileUrl = previewUrl;
      message.fileName = selectedFile.name;
      message.isImage = selectedFile.type.startsWith('image');
    }

    onSendMessage(message);
    setInputValue('');
    setSelectedFile(null);
    setPreviewUrl(null);
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  const handleEmojiClick = (emojiObject) => {
    setInputValue((prev) => prev + emojiObject.emoji);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const fileUrl = URL.createObjectURL(file);
    setSelectedFile(file);
    setPreviewUrl(fileUrl);
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="flex flex-col h-full bg-white rounded-tl-3xl shadow-lg overflow-hidden relative">
      {/* Header */}
      <div className="flex items-center px-4 py-3 bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow">
        <button onClick={onBack} className="mr-4 p-2 hover:bg-purple-700 rounded-full">
          <ArrowLeft size={20} />
        </button>
        <div>
          <h2 className="text-lg font-bold">{group.name}</h2>
          <p className="text-sm">{group.members.length} members</p>
        </div>
      </div>

      {/* Message List */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3 bg-gray-100">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`max-w-xs px-4 py-2 rounded-lg shadow ${
              msg.from === 'me' ? 'bg-blue-500 text-white self-end ml-auto' : 'bg-gray-300 text-black self-start mr-auto'
            }`}
          >
            {msg.isImage ? (
              <img src={msg.fileUrl} alt="sent-img" className="rounded mb-2 max-h-40" />
            ) : msg.fileUrl ? (
              <a href={msg.fileUrl} download className="text-xs underline text-white block mb-1">
                {msg.fileName}
              </a>
            ) : null}
            <p className="text-sm">{msg.text}</p>
            <span className="block text-[10px] mt-1 text-right opacity-70">
              {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
            </span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Preview */}
      {previewUrl && (
        <div className="px-4 py-2 bg-white border-t">
          {selectedFile?.type?.startsWith('image') ? (
            <img src={previewUrl} alt="preview" className="h-32 rounded" />
          ) : (
            <p className="text-sm text-gray-700">Attached: {selectedFile.name}</p>
          )}
        </div>
      )}

      {/* Input */}
      <div className="flex items-center p-3 bg-white border-t relative">
        <button onClick={() => setShowEmojiPicker(!showEmojiPicker)} className="mr-2">
          <Smile className="text-purple-600" />
        </button>
        <button onClick={() => fileInputRef.current.click()} className="mr-2">
          <Paperclip className="text-purple-600" />
        </button>
        <input
          type="file"
          ref={fileInputRef}
          className="hidden"
          onChange={handleFileChange}
        />
        <input
          type="text"
          className="flex-1 p-2 border rounded-full mr-2 focus:outline-none focus:ring-2 focus:ring-purple-400"
          placeholder="Type a message..."
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <button
          onClick={handleSend}
          className="px-4 py-2 bg-purple-600 text-white rounded-full hover:bg-purple-700"
        >
          Send
        </button>

        {showEmojiPicker && (
          <div className="absolute bottom-16 left-4 z-50">
            <EmojiPicker onEmojiClick={handleEmojiClick} />
          </div>
        )}
      </div>
    </div>
  );
};

export default GroupChat;
