import React from 'react';
import { Phone, User } from 'lucide-react';

const Header = ({ onCall, onProfile, isOnline = false }) => {
  return (
    <div className="flex items-center justify-between p-4 bg-[#1f1b2e] text-white">
      <h1 className="text-xl font-bold">Chat App</h1>
      <div className="flex items-center gap-6">
        <button onClick={onCall} className="hover:text-[#6949FF]">
          <Phone size={24} />
        </button>

        <button
          onClick={onProfile}
          className="relative flex items-center hover:text-[#6949FF]"
          aria-label="User Profile"
        >
          <User size={24} />
          {/* Status Indicator */}
          <span
            className={`absolute top-0 right-0 block w-3 h-3 rounded-full border-2 border-[#1f1b2e] ${
              isOnline ? 'bg-green-500' : 'bg-gray-400'
            }`}
            title={isOnline ? 'Online' : 'Offline'}
          />
        </button>
      </div>
    </div>
  );
};

export default Header;
