// ChatWindow.js
import React, { useState } from 'react';
import Picker from 'emoji-picker-react';

const ChatWindow = ({ selectedItem, messages, onSendMessage }) => {
  const [inputValue, setInputValue] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleSend = () => {
    if (selectedFile) {
      onSendMessage(selectedItem.data.name, {
        from: 'me',
        type: 'file',
        file: selectedFile,
        timestamp: new Date().toISOString(),
      });
      setSelectedFile(null);
    } else if (inputValue.trim()) {
      onSendMessage(selectedItem.data.name, {
        from: 'me',
        type: 'text',
        text: inputValue,
        timestamp: new Date().toISOString(),
      });
      setInputValue('');
    }
  };

  const handleEmojiClick = (e, emojiObject) => {
    setInputValue((prev) => prev + emojiObject.emoji);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) setSelectedFile(file);
  };

  return (
    <div className="flex flex-col h-full bg-white">
      <div className="flex-1 overflow-y-auto p-4 space-y-2">
        {messages.map((msg, index) => (
          <div key={index} className={`p-2 rounded-md ${msg.from === 'me' ? 'bg-blue-100 self-end' : 'bg-gray-100 self-start'}`}>
            {msg.type === 'file' && msg.file ? (
              msg.file.type.startsWith('image/') ? (
                <img src={URL.createObjectURL(msg.file)} alt="sent" className="rounded max-w-xs" />
              ) : (
                <a href={URL.createObjectURL(msg.file)} download={msg.file.name} className="text-blue-600 underline">
                  {msg.file.name}
                </a>
              )
            ) : msg.type === 'audio' && msg.audio ? (
              <audio controls className="w-full">
                <source src={URL.createObjectURL(msg.audio)} type="audio/webm" />
              </audio>
            ) : (
              <p>{msg.text}</p>
            )}
          </div>
        ))}
      </div>

      <div className="p-3 border-t flex items-center space-x-2">
        <input type="file" onChange={handleFileChange} className="hidden" id="file-input" />
        <label htmlFor="file-input" className="cursor-pointer text-gray-600">ðŸ“Ž</label>

        <button onClick={() => setShowEmojiPicker((prev) => !prev)}>ðŸ˜Š</button>
        {showEmojiPicker && (
          <div className="absolute bottom-16">
            <Picker onEmojiClick={handleEmojiClick} disableAutoFocus native />
          </div>
        )}

        <input
          type="text"
          className="flex-1 border rounded p-2"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        />
        <button onClick={handleSend} className="bg-blue-500 text-white px-4 py-2 rounded">Send</button>
      </div>
    </div>
  );
};

export default ChatWindow;
