import React, { useState, useRef, useEffect } from 'react';
import { ArrowLeft, Mic, Send, X } from 'lucide-react';
import EmojiPicker from 'emoji-picker-react';

const GroupChat = ({ group, messages, onSendMessage, onSendFile, onSendAudio, onBack }) => {
  const [inputValue, setInputValue] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);

  // Audio recording states
  const [isRecording, setIsRecording] = useState(false);
  const [audioURL, setAudioURL] = useState(null);
  const mediaRecorderRef = useRef(null);
  const audioChunksRef = useRef([]);

  const messagesEndRef = useRef(null);

  // Scroll to bottom on new messages
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Send text message
  const handleSendText = () => {
    if (inputValue.trim()) {
      onSendMessage(inputValue.trim());
      setInputValue('');
      setShowEmojiPicker(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendText();
    }
  };

  // Emoji picker handler
  const onEmojiClick = (emojiData) => {
    setInputValue((prev) => prev + emojiData.emoji);
  };

  // File input handler
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      onSendFile(file);
    }
    e.target.value = ''; // reset input
  };

  // Audio recording logic
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorderRef.current = new MediaRecorder(stream);
      audioChunksRef.current = [];

      mediaRecorderRef.current.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunksRef.current.push(event.data);
        }
      };

      mediaRecorderRef.current.onstop = () => {
        const blob = new Blob(audioChunksRef.current, { type: 'audio/webm' });
        const url = URL.createObjectURL(blob);
        setAudioURL(url);
      };

      mediaRecorderRef.current.start();
      setIsRecording(true);
      setAudioURL(null);
    } catch (err) {
      console.error('Microphone permission denied:', err);
      alert('Please allow microphone access to record audio.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach((track) => track.stop());
      setIsRecording(false);
    }
  };

  const sendRecording = () => {
    if (!audioURL) return;

    fetch(audioURL)
      .then((res) => res.blob())
      .then((blob) => {
        onSendAudio(blob);
        setAudioURL(null);
      });
  };

  const cancelRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach((track) => track.stop());
    }
    setAudioURL(null);
    setIsRecording(false);
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-tl-3xl shadow-lg overflow-hidden">
      {/* Header */}
      <div className="flex items-center px-4 py-3 bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow">
        <button onClick={onBack} className="mr-4 p-2 hover:bg-purple-700 rounded-full">
          <ArrowLeft size={20} />
        </button>
        <div>
          <h2 className="text-lg font-bold">{group.name}</h2>
          <p className="text-sm">{group.members.length} members</p>
        </div>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3 bg-gray-100">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`max-w-xs px-4 py-2 rounded-lg shadow break-words ${
              msg.from === 'me'
                ? 'bg-blue-500 text-white self-end ml-auto'
                : 'bg-gray-300 text-black self-start mr-auto'
            }`}
          >
            {msg.type === 'text' && <p className="text-sm">{msg.text}</p>}

            {msg.type === 'file' && (
              <a
                href={URL.createObjectURL(msg.file)}
                download={msg.file.name}
                className="text-sm underline"
                target="_blank"
                rel="noreferrer"
              >
                📎 {msg.file.name}
              </a>
            )}

            {msg.type === 'image' && (
              <img
                src={URL.createObjectURL(msg.file)}
                alt="Sent"
                className="max-w-full rounded-lg cursor-pointer"
              />
            )}

            {msg.type === 'audio' && (
              <audio controls src={URL.createObjectURL(msg.file)} className="w-48" />
            )}

            <span className="block text-[10px] mt-1 text-right opacity-70">
              {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
            </span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <div className="p-3 border-t bg-white flex flex-col">
        {/* Emoji picker toggle and controls */}
        <div className="flex items-center mb-2">
          <button
            onClick={() => setShowEmojiPicker((v) => !v)}
            className="mr-2 p-2 hover:bg-gray-200 rounded-full"
            title="Emoji Picker"
          >
            😊
          </button>

          {/* File upload */}
          <input
            type="file"
            id="fileInput"
            style={{ display: 'none' }}
            onChange={handleFileChange}
          />
          <label htmlFor="fileInput" className="cursor-pointer mr-2 p-2 hover:bg-gray-200 rounded-full" title="Attach File">
            📎
          </label>

          {/* Mic button */}
          {!isRecording && !audioURL && (
            <button
              onClick={startRecording}
              className="p-2 hover:bg-gray-200 rounded-full"
              title="Record Audio"
            >
              <Mic size={20} />
            </button>
          )}

          {/* Send or Cancel when recording finished */}
          {audioURL && (
            <>
              <button
                onClick={sendRecording}
                className="ml-auto mr-2 p-2 bg-green-500 text-white rounded-full hover:bg-green-600"
                title="Send Recording"
              >
                <Send size={20} />
              </button>
              <button
                onClick={cancelRecording}
                className="p-2 bg-red-500 text-white rounded-full hover:bg-red-600"
                title="Cancel Recording"
              >
                <X size={20} />
              </button>
            </>
          )}

          {/* Stop recording button while recording */}
          {isRecording && (
            <button
              onClick={stopRecording}
              className="ml-auto p-2 bg-red-500 text-white rounded-full hover:bg-red-600"
              title="Stop Recording"
            >
              <X size={20} />
            </button>
          )}
        </div>

        {/* Emoji Picker */}
        {showEmojiPicker && (
          <div className="mb-2">
            <EmojiPicker onEmojiClick={onEmojiClick} />
          </div>
        )}

        {/* Text input & send */}
        {!audioURL && (
          <div className="flex">
            <textarea
              rows={1}
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyDown={handleKeyDown}
              placeholder="Type a message..."
              className="flex-1 resize-none border rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-purple-600"
            />
            <button
              onClick={handleSendText}
              disabled={!inputValue.trim()}
              className={`ml-2 p-2 rounded-full ${
                inputValue.trim() ? 'bg-purple-600 text-white hover:bg-purple-700' : 'bg-gray-300 cursor-not-allowed'
              }`}
              title="Send Message"
            >
              <Send size={20} />
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default GroupChat;
