import React from 'react';

const UserProfile = ({ user }) => {
  return (
    <div className="p-6 bg-white rounded-xl shadow-md max-w-sm mx-auto">
      <div className="flex flex-col items-center">
        <img
          src={user.avatar}
          alt="avatar"
          className="w-24 h-24 rounded-full border-4 border-[#6949FF] shadow-md"
        />
        <h2 className="mt-4 text-xl font-bold text-gray-800">{user.name}</h2>
        <p className="text-sm text-gray-500">{user.email}</p>
        <span className="mt-2 px-3 py-1 bg-green-100 text-green-600 text-xs rounded-full">
          Online
        </span>
        <div className="mt-4 flex gap-3">
          <button className="px-4 py-2 bg-[#6949FF] text-white rounded-full shadow hover:bg-[#5a3be3]">
            Message
          </button>
          <button className="px-4 py-2 border border-[#6949FF] text-[#6949FF] rounded-full hover:bg-[#f3f0ff]">
            View Profile
          </button>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
