import React, { useState, useRef, useEffect } from 'react';
import { ArrowLeft, Mic, Send, X } from 'lucide-react';

const GroupChat = ({ group, messages, onSendMessage, onBack }) => {
  const [inputValue, setInputValue] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioChunks, setAudioChunks] = useState([]);
  const [fileUploading, setFileUploading] = useState(false);
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Start audio recording
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      recorder.start();
      setIsRecording(true);
      setAudioChunks([]);

      recorder.ondataavailable = (e) => {
        if (e.data.size > 0) {
          setAudioChunks((prev) => [...prev, e.data]);
        }
      };

      recorder.onstop = () => {
        // do nothing here, wait for send or cancel
      };

      setMediaRecorder(recorder);
    } catch (err) {
      console.error('Microphone access denied or not supported', err);
    }
  };

  // Stop recording, triggered by Send or Cut buttons
  const stopRecording = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      mediaRecorder.stream.getTracks().forEach((track) => track.stop());
      setMediaRecorder(null);
      setIsRecording(false);
    }
  };

  // Send recorded audio as message
  const sendAudioMessage = () => {
    const audioBlob = new Blob(audioChunks, { type: 'audio/webm' });
    const reader = new FileReader();
    reader.readAsDataURL(audioBlob);
    reader.onloadend = () => {
      onSendMessage({ type: 'audio', data: reader.result });
    };
    stopRecording();
  };

  // Cancel recording and discard audio chunks
  const cancelRecording = () => {
    setAudioChunks([]);
    stopRecording();
  };

  // Handle normal text send
  const handleSend = () => {
    if (inputValue.trim()) {
      onSendMessage({ type: 'text', data: inputValue.trim() });
      setInputValue('');
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !isRecording) handleSend();
  };

  // Handle file/image upload
  const onFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setFileUploading(true);
    const reader = new FileReader();
    reader.onloadend = () => {
      onSendMessage({
        type: file.type.startsWith('image/') ? 'image' : 'file',
        data: reader.result,
        name: file.name,
      });
      setFileUploading(false);
    };
    reader.readAsDataURL(file);
    e.target.value = null; // reset input
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-tl-3xl shadow-lg overflow-hidden">
      {/* Header */}
      <div className="flex items-center px-4 py-3 bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow">
        <button onClick={onBack} className="mr-4 p-2 hover:bg-purple-700 rounded-full">
          <ArrowLeft size={20} />
        </button>
        <div>
          <h2 className="text-lg font-bold">{group.name}</h2>
          <p className="text-sm">{group.members.length} members</p>
        </div>
      </div>

      {/* Message List */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3 bg-gray-100">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`max-w-xs px-4 py-2 rounded-lg shadow break-words ${
              msg.from === 'me'
                ? 'bg-blue-500 text-white self-end ml-auto'
                : 'bg-gray-300 text-black self-start mr-auto'
            }`}
          >
            {msg.type === 'audio' ? (
              <audio controls src={msg.data} />
            ) : msg.type === 'image' ? (
              <img src={msg.data} alt="sent image" className="max-w-full rounded" />
            ) : msg.type === 'file' ? (
              <a href={msg.data} download={msg.name} className="underline text-blue-700">
                {msg.name}
              </a>
            ) : (
              <p className="text-sm whitespace-pre-wrap">{msg.data}</p>
            )}
            <span className="block text-[10px] mt-1 text-right opacity-70">
              {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
            </span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Input & Controls */}
      <div className="flex items-center p-3 bg-white border-t">
        {/* File input (hidden) */}
        <input
          type="file"
          accept="image/*,application/pdf,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
          className="hidden"
          ref={fileInputRef}
          onChange={onFileChange}
        />
        {/* File upload button */}
        <button
          onClick={() => fileInputRef.current?.click()}
          disabled={isRecording || fileUploading}
          className="mr-2 p-2 rounded-full hover:bg-gray-200"
          title="Upload file or image"
        >
          ðŸ“Ž
        </button>

        {/* Emoji picker button (replace with your emoji picker trigger) */}
        <button
          disabled={isRecording}
          className="mr-2 p-2 rounded-full hover:bg-gray-200"
          title="Pick emoji"
          // Add your emoji picker open handler here
          onClick={() => alert('Open emoji picker (implement as needed)')}
        >
          ðŸ˜€
        </button>

        {/* Text input */}
        <input
          type="text"
          className="flex-1 p-2 border rounded-full mr-2 focus:outline-none focus:ring-2 focus:ring-purple-400"
          placeholder={isRecording ? 'Recording audio...' : 'Type a message...'}
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={handleKeyDown}
          disabled={isRecording}
        />

        {/* Mic / Recording Controls */}
        {!isRecording ? (
          <button
            onClick={startRecording}
            className="p-2 rounded-full bg-purple-600 text-white hover:bg-purple-700"
            title="Start recording audio message"
          >
            <Mic size={24} />
          </button>
        ) : (
          <div className="flex items-center space-x-2">
            <span className="text-red-600 font-semibold animate-pulse">Recording...</span>

            <button
              onClick={sendAudioMessage}
              className="p-2 rounded-full bg-green-600 text-white hover:bg-green-700"
              title="Send audio message"
            >
              <Send size={24} />
            </button>

            <button
              onClick={cancelRecording}
              className="p-2 rounded-full bg-red-600 text-white hover:bg-red-700"
              title="Cancel recording"
            >
              <X size={24} />
            </button>
          </div>
        )}

        {/* Normal Send Button (disabled while recording) */}
        <button
          onClick={handleSend}
          disabled={isRecording || inputValue.trim() === ''}
          className="px-4 py-2 bg-purple-600 text-white rounded-full hover:bg-purple-700 ml-2"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default GroupChat;
