import React, { useState, useRef, useEffect } from 'react';
import { ArrowLeft, Send, Paperclip, Mic, Smile, X } from 'lucide-react';
import EmojiPicker from 'emoji-picker-react';

const ChatWindow = ({ selectedItem, messages, onSendMessage }) => {
  const [input, setInput] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [recording, setRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = () => {
    if (!input.trim()) return;

    onSendMessage({
      from: 'me',
      type: 'text',
      text: input.trim(),
      timestamp: new Date().toISOString(),
    });

    setInput('');
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') handleSend();
  };

  const onEmojiClick = (emojiData) => {
    setInput((prev) => prev + emojiData.emoji);
    setShowEmojiPicker(false);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onloadend = () => {
      onSendMessage({
        from: 'me',
        type: 'file',
        file: {
          name: file.name,
          type: file.type,
          url: reader.result,
        },
        timestamp: new Date().toISOString(),
      });
    };
    reader.readAsDataURL(file);
  };

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const recorder = new MediaRecorder(stream);
    const chunks = [];

    recorder.ondataavailable = (e) => chunks.push(e.data);
    recorder.onstop = () => {
      const audioBlob = new Blob(chunks, { type: 'audio/webm' });
      const audioUrl = URL.createObjectURL(audioBlob);
      onSendMessage({
        from: 'me',
        type: 'audio',
        audioURL: audioUrl,
        timestamp: new Date().toISOString(),
      });
    };

    recorder.start();
    setMediaRecorder(recorder);
    setRecording(true);
  };

  const stopRecording = () => {
    mediaRecorder.stop();
    setRecording(false);
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-tl-3xl shadow-lg overflow-hidden">
      <div className="flex items-center px-4 py-3 bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow">
        <button
          onClick={() => window.history.back()}
          className="mr-4 p-2 hover:bg-purple-700 rounded-full"
        >
          <ArrowLeft size={20} />
        </button>
        <div>
          <h2 className="text-lg font-bold">{selectedItem.data.name}</h2>
          <p className="text-sm text-purple-200">{selectedItem.data.email}</p>
        </div>
      </div>

      <div className="flex-1 overflow-y-auto p-4 space-y-3 bg-gray-100">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`max-w-xs px-4 py-2 rounded-lg shadow break-words ${
              msg.from === 'me'
                ? 'bg-blue-500 text-white self-end ml-auto'
                : 'bg-gray-300 text-black self-start mr-auto'
            }`}
          >
            {msg.type === 'text' && <p className="text-sm">{msg.text}</p>}
            {msg.type === 'file' && (
              <a
                href={msg.file.url}
                download={msg.file.name}
                className="text-sm underline text-white"
              >
                📎 {msg.file.name}
              </a>
            )}
            {msg.type === 'audio' && (
              <audio controls src={msg.audioURL} className="w-full" />
            )}
            <span className="block text-[10px] mt-1 text-right opacity-70">
              {new Date(msg.timestamp).toLocaleTimeString([], {
                hour: '2-digit',
                minute: '2-digit',
              })}
            </span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="relative p-3 bg-white border-t flex items-center gap-2">
        <button
          onClick={() => setShowEmojiPicker((prev) => !prev)}
          className="p-2 rounded-full hover:bg-gray-200"
          title="Insert Emoji"
        >
          <Smile size={20} />
        </button>

        <button
          onClick={() => fileInputRef.current.click()}
          className="p-2 rounded-full hover:bg-gray-200"
          title="Attach File"
        >
          <Paperclip size={20} />
        </button>

        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileChange}
          className="hidden"
        />

        <input
          type="text"
          className="flex-1 p-2 border rounded-full focus:outline-none focus:ring-2 focus:ring-purple-400"
          placeholder="Type a message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
        />

        {recording ? (
          <button
            onClick={stopRecording}
            className="p-2 bg-red-600 text-white rounded-full hover:bg-red-700"
            title="Stop Recording"
          >
            <Mic size={20} />
          </button>
        ) : (
          <button
            onClick={startRecording}
            className="p-2 rounded-full hover:bg-gray-200"
            title="Record Audio"
          >
            <Mic size={20} />
          </button>
        )}

        <button
          onClick={handleSend}
          className="p-2 bg-purple-600 text-white rounded-full hover:bg-purple-700"
          title="Send Message"
        >
          <Send size={20} />
        </button>

        {/* Emoji Picker */}
        {showEmojiPicker && (
          <div className="absolute bottom-16 left-2 shadow-lg z-10">
            <EmojiPicker onEmojiClick={onEmojiClick} />
            <button
              className="absolute top-1 right-1 text-gray-600 hover:text-gray-900"
              onClick={() => setShowEmojiPicker(false)}
              title="Close Emoji Picker"
            >
              <X size={16} />
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatWindow;
