import React, { useState } from 'react';

const Sidebar = () => {
  const [search, setSearch] = useState('');
  const users = [
    { name: 'John Doe', status: 'Online' },
    { name: 'Jane Smith', status: 'Offline' },
    { name: 'Mark Evans', status: 'Online' },
    { name: 'Emily White', status: 'Offline' },
  ];

  const groups = [
    { name: 'Project Alpha' },
    { name: 'Friends' },
    { name: 'Work Chat' },
  ];

  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="h-full flex flex-col bg-[#F7F7F8] border-r border-gray-200">
      {/* Header */}
      <div className="p-5 border-b border-gray-200">
        <h2 className="text-2xl font-bold text-[#6949FF]">Chats</h2>
      </div>

      {/* Create New Group */}
      <div className="px-5 py-3 border-b border-gray-200">
        <button
          type="button"
          className="w-full py-2 bg-[#6949FF] text-white rounded-lg hover:bg-[#5a3be3] transition"
          onClick={() => alert('Create New Group clicked')} // Replace with your handler
        >
          + Create New Group
        </button>
      </div>

      {/* Search Box */}
      <div className="px-4 py-3 border-b border-gray-200">
        <input
          type="text"
          placeholder="Search users..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#6949FF] bg-white"
        />
      </div>

      {/* User List */}
      <div className="flex-1 overflow-y-auto px-4">
        <h3 className="mt-4 mb-2 text-sm font-semibold text-gray-500 uppercase">
          Users
        </h3>
        {filteredUsers.length > 0 ? (
          filteredUsers.map((user, idx) => (
            <div
              key={idx}
              className="flex items-center gap-3 p-3 border-b border-gray-100 hover:bg-[#ECEBFF] transition cursor-pointer rounded-md"
            >
              <div className="w-10 h-10 bg-[#6949FF] text-white rounded-full flex items-center justify-center font-semibold">
                {user.name.charAt(0)}
              </div>
              <div>
                <p className="font-medium text-gray-800">{user.name}</p>
                <p
                  className={`text-sm ${
                    user.status === 'Online' ? 'text-green-500' : 'text-gray-400'
                  }`}
                >
                  {user.status}
                </p>
              </div>
            </div>
          ))
        ) : (
          <p className="text-gray-400 text-sm">No users found.</p>
        )}

        {/* Groups List */}
        <h3 className="mt-6 mb-2 text-sm font-semibold text-gray-500 uppercase">
          Groups
        </h3>
        {groups.length > 0 ? (
          groups.map((group, idx) => (
            <div
              key={idx}
              className="flex items-center gap-3 p-3 border-b border-gray-100 hover:bg-[#ECEBFF] transition cursor-pointer rounded-md"
            >
              <div className="w-10 h-10 bg-[#a78bfa] text-white rounded-full flex items-center justify-center font-semibold">
                {group.name.charAt(0)}
              </div>
              <p className="font-medium text-gray-800">{group.name}</p>
            </div>
          ))
        ) : (
          <p className="text-gray-400 text-sm">No groups available.</p>
        )}
      </div>
    </div>
  );
};

export default Sidebar;
