import React, { useState, useRef, useEffect } from 'react';
import { ArrowLeft, Send, Mic, X } from 'lucide-react';
import EmojiPicker from 'emoji-picker-react';

const GroupChat = ({ group, messages, onSendMessage, onBack }) => {
  const [inputValue, setInputValue] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [audioChunks, setAudioChunks] = useState([]);
  const mediaRecorderRef = useRef(null);
  const [audioURL, setAudioURL] = useState(null);

  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Emoji picker handler
  const onEmojiClick = (emojiData) => {
    setInputValue((prev) => prev + emojiData.emoji);
  };

  // File upload handler
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = () => {
      onSendMessage(group.name, {
        from: 'me',
        file: {
          name: file.name,
          type: file.type,
          dataURL: reader.result,
        },
        type: 'file',
        timestamp: new Date().toISOString(),
      });
    };
    reader.readAsDataURL(file);
    e.target.value = null;
  };

  // Start recording audio
  const handleStartRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);

      mediaRecorderRef.current = mediaRecorder;
      setAudioChunks([]);

      mediaRecorder.ondataavailable = (e) => {
        if (e.data.size > 0) {
          setAudioChunks((prev) => [...prev, e.data]);
        }
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(audioChunks, { type: 'audio/webm' });
        const url = URL.createObjectURL(blob);
        setAudioURL(url);
      };

      mediaRecorder.start();
      setIsRecording(true);
      setAudioURL(null);
    } catch (error) {
      console.error('Error accessing microphone:', error);
      alert('Please allow microphone access to record audio.');
    }
  };

  // Stop recording and discard audio
  const handleCancelRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach((track) => track.stop());
    }
    setIsRecording(false);
    setAudioChunks([]);
    setAudioURL(null);
  };

  // Stop recording and send audio message
  const handleSendRecording = () => {
    if (mediaRecorderRef.current && mediaRecorderRef.current.state !== 'inactive') {
      mediaRecorderRef.current.stop();
      mediaRecorderRef.current.stream.getTracks().forEach((track) => track.stop());
    }

    // Wait a tick to ensure 'onstop' fired and audioURL is set
    setTimeout(() => {
      if (!audioURL) {
        alert('Recording not ready yet. Please try again.');
        return;
      }
      onSendMessage(group.name, {
        from: 'me',
        type: 'audio',
        audioURL,
        timestamp: new Date().toISOString(),
      });

      setIsRecording(false);
      setAudioChunks([]);
      setAudioURL(null);
      mediaRecorderRef.current = null;
    }, 100);
  };

  // Send text message
  const handleSend = () => {
    if (!inputValue.trim()) return;
    onSendMessage(group.name, {
      from: 'me',
      text: inputValue.trim(),
      type: 'text',
      timestamp: new Date().toISOString(),
    });
    setInputValue('');
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-tl-3xl shadow-lg overflow-hidden relative">
      {/* Header */}
      <div className="flex items-center px-4 py-3 bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow">
        <button onClick={onBack} className="mr-4 p-2 hover:bg-purple-700 rounded-full">
          <ArrowLeft size={20} />
        </button>
        <div>
          <h2 className="text-lg font-bold">{group.name}</h2>
          <p className="text-sm">{group.members.length} members</p>
        </div>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3 bg-gray-100">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`max-w-xs px-4 py-2 rounded-lg shadow break-words ${
              msg.from === 'me' ? 'bg-blue-500 text-white self-end ml-auto' : 'bg-gray-300 text-black self-start mr-auto'
            }`}
          >
            {msg.type === 'text' && <p className="whitespace-pre-wrap">{msg.text}</p>}

            {msg.type === 'file' && (
              <>
                {msg.file.type.startsWith('image/') ? (
                  <img src={msg.file.dataURL} alt={msg.file.name} className="max-w-full rounded" />
                ) : (
                  <a href={msg.file.dataURL} download={msg.file.name} className="underline text-blue-700">
                    Download {msg.file.name}
                  </a>
                )}
              </>
            )}

            {msg.type === 'audio' && (
              <audio controls src={msg.audioURL}>
                Your browser does not support the audio element.
              </audio>
            )}

            <span className="block text-[10px] mt-1 text-right opacity-70">
              {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
            </span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Emoji Picker */}
      {showEmojiPicker && (
        <div className="absolute bottom-20 right-5 z-50">
          <EmojiPicker onEmojiClick={onEmojiClick} />
        </div>
      )}

      {/* Input Area */}
      <div className="flex items-center p-3 bg-white border-t relative">
        <button
          onClick={() => setShowEmojiPicker((val) => !val)}
          className="mr-2 p-2 hover:bg-gray-200 rounded-full"
          title="Emoji picker"
          type="button"
        >
          😊
        </button>

        <label
          htmlFor="fileInput"
          className="mr-2 p-2 hover:bg-gray-200 rounded-full cursor-pointer"
          title="Attach file"
        >
          📎
        </label>
        <input
          id="fileInput"
          type="file"
          accept="image/*,application/pdf,audio/*"
          className="hidden"
          onChange={handleFileChange}
        />

        {!isRecording ? (
          <textarea
            rows={1}
            className="flex-1 p-2 border rounded-full mr-2 resize-none focus:outline-none focus:ring-2 focus:ring-purple-400"
            placeholder="Type a message..."
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyDown={handleKeyDown}
          />
        ) : (
          <div className="flex flex-1 items-center justify-between bg-red-100 border border-red-500 rounded-full px-4 py-2 mr-2">
            <span className="text-red-600 font-semibold">Recording...</span>
            <div className="flex space-x-3">
              <button
                onClick={handleSendRecording}
                className="text-green-600 hover:text-green-800"
                title="Send recording"
              >
                <Send size={24} />
              </button>
              <button
                onClick={handleCancelRecording}
                className="text-red-600 hover:text-red-800"
                title="Cancel recording"
              >
                <X size={24} />
              </button>
            </div>
          </div>
        )}

        {!isRecording && (
          <button
            onClick={handleStartRecording}
            className="p-2 hover:bg-gray-200 rounded-full"
            title="Record audio"
            type="button"
          >
            <Mic size={20} />
          </button>
        )}

        {!isRecording && (
          <button
            onClick={handleSend}
            className="ml-2 p-2 bg-purple-600 hover:bg-purple-700 rounded-full text-white"
            title="Send message"
            type="button"
          >
            <Send size={20} />
          </button>
        )}
      </div>
    </div>
  );
};

export default GroupChat;
