import React from 'react';

const ChatWindow = ({ selectedItem }) => {
  if (!selectedItem) {
    return (
      <div className="flex-1 flex items-center justify-center text-gray-400">
        Select a user or group to start chatting.
      </div>
    );
  }

  if (selectedItem.type === 'user') {
    const { name, status } = selectedItem.data;
    return (
      <div className="flex-1 p-6 bg-white rounded-lg shadow-lg">
        <h2 className="text-xl font-bold mb-4">Chat with {name}</h2>
        <p>Status: {status}</p>
        {/* Render messages or chat UI here */}
      </div>
    );
  }

  if (selectedItem.type === 'group') {
    const { name, members } = selectedItem.data;
    return (
      <div className="flex-1 p-6 bg-white rounded-lg shadow-lg">
        <h2 className="text-xl font-bold mb-4">Group: {name}</h2>
        <p>Members: {members.length}</p>
        {/* Render group chat UI here */}
      </div>
    );
  }

  return null;
};

export default ChatWindow;
