import React, { useState } from 'react';

const Sidebar = () => {
  const [search, setSearch] = useState('');
  const [groups, setGroups] = useState([
    { name: 'Project Alpha' },
    { name: 'Friends' },
    { name: 'Work Chat' },
  ]);
  const [showCreateGroup, setShowCreateGroup] = useState(false);
  const [newGroupName, setNewGroupName] = useState('');
  const [selectedUsers, setSelectedUsers] = useState([]);

  const users = [
    { name: 'John Doe', status: 'Online' },
    { name: 'Jane Smith', status: 'Offline' },
    { name: 'Mark Evans', status: 'Online' },
    { name: 'Emily White', status: 'Offline' },
  ];

  const filteredUsers = users.filter(user =>
    user.name.toLowerCase().includes(search.toLowerCase())
  );

  // Toggle user selection for group
  const toggleUser = (userName) => {
    setSelectedUsers(prev =>
      prev.includes(userName)
        ? prev.filter(u => u !== userName)
        : [...prev, userName]
    );
  };

  const handleCreateGroup = () => {
    if (!newGroupName.trim()) {
      alert('Group name is required');
      return;
    }
    // Add new group
    setGroups(prev => [...prev, { name: newGroupName.trim(), members: selectedUsers }]);
    // Reset modal
    setNewGroupName('');
    setSelectedUsers([]);
    setShowCreateGroup(false);
  };

  return (
    <div className="h-full flex flex-col bg-[#F7F7F8] border-r border-gray-200 relative">
      {/* Header */}
      <div className="p-5 border-b border-gray-200">
        <h2 className="text-2xl font-bold text-[#6949FF]">Chats</h2>
      </div>

      {/* Create New Group */}
      <div className="px-5 py-3 border-b border-gray-200">
        <button
          type="button"
          className="w-full py-2 bg-[#6949FF] text-white rounded-lg hover:bg-[#5a3be3] transition"
          onClick={() => setShowCreateGroup(true)}
        >
          + Create New Group
        </button>
      </div>

      {/* Search Box */}
      <div className="px-4 py-3 border-b border-gray-200">
        <input
          type="text"
          placeholder="Search users..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-[#6949FF] bg-white"
        />
      </div>

      {/* User List */}
      <div className="flex-1 overflow-y-auto px-4">
        <h3 className="mt-4 mb-2 text-sm font-semibold text-gray-500 uppercase">
          Users
        </h3>
        {filteredUsers.length > 0 ? (
          filteredUsers.map((user, idx) => (
            <div
              key={idx}
              className="flex items-center gap-3 p-3 border-b border-gray-100 hover:bg-[#ECEBFF] transition cursor-pointer rounded-md"
            >
              <div className="w-10 h-10 bg-[#6949FF] text-white rounded-full flex items-center justify-center font-semibold">
                {user.name.charAt(0)}
              </div>
              <div>
                <p className="font-medium text-gray-800">{user.name}</p>
                <p
                  className={`text-sm ${
                    user.status === 'Online' ? 'text-green-500' : 'text-gray-400'
                  }`}
                >
                  {user.status}
                </p>
              </div>
            </div>
          ))
        ) : (
          <p className="text-gray-400 text-sm">No users found.</p>
        )}

        {/* Groups List */}
        <h3 className="mt-6 mb-2 text-sm font-semibold text-gray-500 uppercase">
          Groups
        </h3>
        {groups.length > 0 ? (
          groups.map((group, idx) => (
            <div
              key={idx}
              className="flex items-center gap-3 p-3 border-b border-gray-100 hover:bg-[#ECEBFF] transition cursor-pointer rounded-md"
            >
              <div className="w-10 h-10 bg-[#a78bfa] text-white rounded-full flex items-center justify-center font-semibold">
                {group.name.charAt(0)}
              </div>
              <p className="font-medium text-gray-800">{group.name}</p>
            </div>
          ))
        ) : (
          <p className="text-gray-400 text-sm">No groups available.</p>
        )}
      </div>

      {/* Create Group Modal */}
      {showCreateGroup && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex justify-center items-center z-50">
          <div className="bg-white rounded-lg shadow-lg w-80 p-6">
            <h3 className="text-lg font-bold mb-4">Create New Group</h3>
            <input
              type="text"
              placeholder="Group Name"
              value={newGroupName}
              onChange={(e) => setNewGroupName(e.target.value)}
              className="w-full mb-4 px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-[#6949FF]"
            />
            <div className="max-h-40 overflow-y-auto mb-4 border border-gray-200 rounded p-2">
              <p className="font-semibold mb-2">Select Members</p>
              {users.map((user, idx) => (
                <label key={idx} className="flex items-center gap-2 mb-1 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={selectedUsers.includes(user.name)}
                    onChange={() => toggleUser(user.name)}
                    className="form-checkbox h-4 w-4 text-[#6949FF]"
                  />
                  <span>{user.name}</span>
                </label>
              ))}
            </div>
            <div className="flex justify-end gap-3">
              <button
                onClick={() => setShowCreateGroup(false)}
                className="px-4 py-2 rounded bg-gray-300 hover:bg-gray-400 transition"
              >
                Cancel
              </button>
              <button
                onClick={handleCreateGroup}
                className="px-4 py-2 rounded bg-[#6949FF] text-white hover:bg-[#5a3be3] transition"
              >
                Create
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Sidebar;
