import React from 'react';

const ChatWindow = ({ currentChat }) => {
  if (!currentChat) {
    return (
      <div className="flex-1 flex items-center justify-center text-white text-lg">
        Select a user or group to start chatting.
      </div>
    );
  }

  if (currentChat.type === 'user') {
    const user = currentChat.data;
    return (
      <div className="flex-1 p-6 bg-white flex flex-col">
        <h2 className="text-2xl font-bold mb-4">Chat with {user.name}</h2>
        {/* Messages area */}
        <div className="flex-1 overflow-y-auto border rounded p-4 mb-4">
          {/* Placeholder messages */}
          <p className="text-gray-700">[Messages with {user.name} will appear here]</p>
        </div>
        {/* Input box */}
        <div>
          <input
            type="text"
            placeholder={`Message ${user.name}...`}
            className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-[#6949FF]"
          />
        </div>
      </div>
    );
  }

  if (currentChat.type === 'group') {
    const group = currentChat.data;
    return (
      <div className="flex-1 p-6 bg-white flex flex-col">
        <h2 className="text-2xl font-bold mb-2">Group: {group.name}</h2>
        {group.members && group.members.length > 0 && (
          <p className="mb-4 text-gray-600">Members: {group.members.join(', ')}</p>
        )}
        <div className="flex-1 overflow-y-auto border rounded p-4 mb-4">
          <p className="text-gray-700">[Group chat messages will appear here]</p>
        </div>
        <div>
          <input
            type="text"
            placeholder={`Message group ${group.name}...`}
            className="w-full border rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-[#6949FF]"
          />
        </div>
      </div>
    );
  }

  return null;
};

export default ChatWindow;
