import React, { useState } from 'react';

const ChatWindow = () => {
  const [messages, setMessages] = useState([
    { sender: 'John', text: 'Hey! How are you?' },
    { sender: 'You', text: 'Doing great, thanks! You?' },
    { sender: 'John', text: 'I’m good. Want to catch up later?' }
  ]);

  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, { sender: 'You', text: input }]);
      setInput('');
    }
  };

  return (
    <div className="flex flex-col h-full w-full bg-gradient-to-br from-[#ECEBFF] via-[#F1EDFF] to-[#E2DCFF]">
      {/* Header */}
      <div className="p-4 border-b border-gray-300 flex items-center justify-between bg-[#6949FF] text-white">
        <h2 className="font-bold text-lg">John Doe</h2>
        <span className="text-sm opacity-80">Online</span>
      </div>

      {/* Messages */}
      <div className="flex-1 p-4 overflow-y-auto space-y-3">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`max-w-xs px-4 py-2 rounded-lg shadow-sm text-sm ${
              msg.sender === 'You'
                ? 'ml-auto bg-[#6949FF] text-white'
                : 'mr-auto bg-white text-gray-900'
            }`}
          >
            {msg.text}
          </div>
        ))}
      </div>

      {/* Input */}
      <div className="p-4 border-t border-gray-300 bg-white flex gap-2 items-center">
        <input
          type="text"
          className="flex-1 px-4 py-2 rounded-full border border-gray-300 focus:outline-none focus:ring-2 focus:ring-[#6949FF]"
          placeholder="Type a message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSend()}
        />
        <button
          onClick={handleSend}
          className="bg-[#6949FF] text-white px-4 py-2 rounded-full hover:bg-[#5a3be3] transition"
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatWindow;
