import React, { useState, useRef, useEffect } from 'react';

const initialMessages = [
  { text: 'Hi there!', isOwn: false },
  { text: 'Hello! How are you?', isOwn: true },
];

function ChatWindow() {
  const [messages, setMessages] = useState(initialMessages);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  const sendMessage = () => {
    if (!input.trim()) return;
    setMessages([...messages, { text: input, isOwn: true }]);
    setInput('');
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="flex-1 flex flex-col bg-[#f5f5fa] h-screen">
      <div className="flex-1 overflow-y-auto px-6 py-4 space-y-4 scroll-smooth">
        {messages.map((msg, index) => (
          <div
            key={index}
            className={`p-3 rounded-xl max-w-xs shadow-md ${
              msg.isOwn
                ? 'bg-indigo-600 text-white self-end'
                : 'bg-white text-gray-800 self-start'
            } hover:scale-105 transform transition`}
          >
            {msg.text}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <div className="p-4 bg-white flex items-center gap-2 shadow-inner">
        <input
          type="text"
          className="flex-1 p-3 rounded-full border border-gray-300 outline-none focus:ring-2 focus:ring-indigo-500"
          placeholder="Type your message..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && sendMessage()}
        />
        <button
          onClick={sendMessage}
          className="px-4 py-2 bg-indigo-600 text-white rounded-full hover:bg-indigo-700 transition"
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default ChatWindow;
