import React from 'react';

const Sidebar = () => {
  const users = [
    { name: 'John Doe', status: 'Online' },
    { name: 'Jane Smith', status: 'Offline' },
    { name: 'Mark Evans', status: 'Online' }
  ];

  return (
    <div className="h-full flex flex-col">
      <div className="p-4 border-b">
        <h2 className="text-xl font-bold">Chats</h2>
      </div>
      <div className="flex-1 overflow-y-auto">
        {users.map((user, idx) => (
          <div
            key={idx}
            className="flex items-center justify-between p-4 border-b hover:bg-gray-100 cursor-pointer transition"
          >
            <div>
              <p className="font-medium">{user.name}</p>
              <span className={`text-sm ${user.status === 'Online' ? 'text-green-500' : 'text-gray-400'}`}>
                {user.status}
              </span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Sidebar;
