// App.js (Parent + GroupChat combined minimal example)

import React, { useState, useEffect, useRef } from 'react';
import EmojiPicker from 'emoji-picker-react';

// Helper component for displaying messages with types
const Message = ({ msg }) => {
  if (msg.type === 'text' || msg.type === 'emoji') {
    return (
      <div className={`max-w-xs px-4 py-2 rounded-lg shadow ${
        msg.from === 'me' ? 'bg-blue-500 text-white self-end ml-auto' : 'bg-gray-300 text-black self-start mr-auto'
      }`}>
        <p className="text-sm">{msg.data}</p>
        <span className="block text-[10px] mt-1 text-right opacity-70">
          {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
        </span>
      </div>
    );
  } else if (msg.type === 'file') {
    return (
      <div className={`max-w-xs p-2 rounded-lg shadow ${
        msg.from === 'me' ? 'bg-blue-500 text-white self-end ml-auto' : 'bg-gray-300 text-black self-start mr-auto'
      }`}>
        <a href={msg.data} target="_blank" rel="noreferrer" className="underline">
          Download File
        </a>
        <span className="block text-[10px] mt-1 text-right opacity-70">
          {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
        </span>
      </div>
    );
  } else if (msg.type === 'image') {
    return (
      <div className={`max-w-xs rounded-lg shadow ${
        msg.from === 'me' ? 'self-end ml-auto' : 'self-start mr-auto'
      }`}>
        <img src={msg.data} alt="Sent" className="max-w-full rounded-lg" />
        <span className="block text-[10px] mt-1 text-right opacity-70">
          {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
        </span>
      </div>
    );
  } else if (msg.type === 'audio') {
    return (
      <div className={`max-w-xs p-2 rounded-lg shadow ${
        msg.from === 'me' ? 'bg-blue-500 text-white self-end ml-auto' : 'bg-gray-300 text-black self-start mr-auto'
      }`}>
        <audio controls src={msg.data}></audio>
        <span className="block text-[10px] mt-1 text-right opacity-70">
          {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
        </span>
      </div>
    );
  }

  return null;
};

const GroupChat = ({ group, messages, onSendMessage, onBack }) => {
  const [inputValue, setInputValue] = useState('');
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [recording, setRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioChunks, setAudioChunks] = useState([]);
  const [audioBlobUrl, setAudioBlobUrl] = useState(null);

  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Emoji picker handler
  const onEmojiClick = (event, emojiObject) => {
    setInputValue((prev) => prev + emojiObject.emoji);
  };

  // File upload handler
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    if (file.type.startsWith('image/')) {
      const reader = new FileReader();
      reader.onload = (e) => {
        onSendMessage({ type: 'image', data: e.target.result });
      };
      reader.readAsDataURL(file);
    } else {
      // For other files, create object URL for download
      const url = URL.createObjectURL(file);
      onSendMessage({ type: 'file', data: url });
    }
    e.target.value = ''; // reset file input
  };

  // Start audio recording
  const startRecording = async () => {
    if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
      alert('Audio recording not supported in this browser');
      return;
    }

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      let chunks = [];

      recorder.ondataavailable = (e) => {
        chunks.push(e.data);
      };

      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/webm' });
        const url = URL.createObjectURL(blob);
        setAudioBlobUrl(url);
        setAudioChunks(chunks);
        setRecording(false);
        // Store blob for sending on confirmation
        setRecordedBlob(blob);
      };

      recorder.start();
      setMediaRecorder(recorder);
      setAudioChunks(chunks);
      setRecording(true);
    } catch (err) {
      alert('Could not start audio recording');
    }
  };

  // Stop recording and keep audioBlobUrl for preview
  const stopRecording = () => {
    if (mediaRecorder) mediaRecorder.stop();
  };

  // Cancel recording (discard)
  const cancelRecording = () => {
    if (mediaRecorder && recording) mediaRecorder.stop();
    setAudioBlobUrl(null);
    setAudioChunks([]);
    setRecording(false);
    setRecordedBlob(null);
  };

  const [recordedBlob, setRecordedBlob] = useState(null);

  // Send recorded audio message
  const sendAudioMessage = () => {
    if (!recordedBlob) return;
    const url = URL.createObjectURL(recordedBlob);
    onSendMessage({ type: 'audio', data: url });
    setAudioBlobUrl(null);
    setRecordedBlob(null);
  };

  const handleSend = () => {
    if (inputValue.trim()) {
      onSendMessage({ type: 'text', data: inputValue.trim() });
      setInputValue('');
      setShowEmojiPicker(false);
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !recording) {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-tl-3xl shadow-lg overflow-hidden">
      {/* Header */}
      <div className="flex items-center px-4 py-3 bg-gradient-to-r from-purple-600 to-pink-600 text-white shadow">
        <button onClick={onBack} className="mr-4 p-2 hover:bg-purple-700 rounded-full">
          ‚Üê
        </button>
        <div>
          <h2 className="text-lg font-bold">{group.name}</h2>
          <p className="text-sm">{group.members.length} members</p>
        </div>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-3 bg-gray-100">
        {messages.map((msg, index) => (
          <Message key={index} msg={msg} />
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Input & controls */}
      <div className="p-3 bg-white border-t flex items-center space-x-2 relative">
        {/* Emoji Picker Toggle */}
        <button
          className="p-2 hover:bg-gray-200 rounded"
          onClick={() => setShowEmojiPicker((val) => !val)}
          disabled={recording}
          aria-label="Toggle Emoji Picker"
        >
          üòä
        </button>

        {/* File Upload */}
        <label htmlFor="fileInput" className="p-2 cursor-pointer hover:bg-gray-200 rounded" aria-label="Upload File or Image" disabled={recording}>
          üìé
          <input
            id="fileInput"
            type="file"
            onChange={handleFileChange}
            className="hidden"
            disabled={recording}
            accept="image/*,application/pdf,audio/*,video/*,.doc,.docx,.xls,.xlsx,.ppt,.pptx,.txt"
          />
        </label>

        {/* Text Input */}
        <input
          type="text"
          className="flex-1 p-2 border rounded-full focus:outline-none focus:ring-2 focus:ring-purple-400"
          placeholder={recording ? 'Recording audio...' : 'Type a message...'}
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyDown={handleKeyDown}
          disabled={recording}
          aria-label="Message input"
        />

        {/* Recording controls */}
        {!recording && !audioBlobUrl && (
          <button
            className="p-2 text-red-600 hover:bg-gray-200 rounded"
            onClick={startRecording}
            aria-label="Start Audio Recording"
          >
            üé§
          </button>
        )}

        {recording && (
          <>
            <button
              onClick={stopRecording}
              className="p-2 bg-green-500 text-white rounded hover:bg-green-600"
              aria-label="Stop Recording"
            >
              ‚èπÔ∏è
            </button>
            <button
              onClick={cancelRecording}
              className="p-2 bg-red-500 text-white rounded hover:bg-red-600"
              aria-label="Cancel Recording"
            >
              ‚ùå
            </button>
          </>
        )}

        {audioBlobUrl && (
          <>
            <audio controls src={audioBlobUrl} className="mr-2" />
            <button
              onClick={sendAudioMessage}
              className="p-2 bg-blue-600 text-white rounded hover:bg-blue-700"
              aria-label="Send Audio Message"
            >
              ‚û§
            </button>
            <button
              onClick={cancelRecording}
              className="p-2 bg-red-500 text-white rounded hover:bg-red-600"
              aria-label="Cancel Audio"
            >
              ‚ùå
            </button>
          </>
        )}

        {/* Send Button */}
        {!recording && !audioBlobUrl && (
          <button
            onClick={handleSend}
            className="px-4 py-2 bg-purple-600 text-white rounded-full hover:bg-purple-700"
            aria-label="Send Message"
          >
            Send
          </button>
        )}

        {/* Emoji Picker */}
        {showEmojiPicker && (
          <div className="absolute bottom-14 left-2 z-50">
            <EmojiPicker onEmojiClick={onEmojiClick} />
          </div>
        )}
      </div>
    </div>
  );
};

const App = () => {
  const [messages, setMessages] = useState([]);

  // OnSendMessage receives { type, data }
  const handleSendMessage = (msg) => {
    const newMsg = {
      ...msg,
      from: 'me',
      timestamp: Date.now(),
    };
    setMessages((prev) => [...prev, newMsg]);
  };

  const dummyGroup = {
    name: 'React Developers',
    members: ['Alice', 'Bob', 'Charlie'],
  };

  return (
    <div className="h-screen flex justify-center items-center bg-gray-50">
      <div className="w-full max-w-md h-full max-h-[600px] border rounded-xl shadow-lg flex flex-col">
        <GroupChat
          group={dummyGroup}
          messages={messages}
          onSendMessage={handleSendMessage}
          onBack={() => alert('Back clicked')}
        />
      </div>
    </div>
  );
};

export default App;
