import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import ChatWindow from './components/ChatWindow';
import GroupChat from './components/GroupChat';
import CallWindow from './components/CallWindow';
import UserProfile from './components/UserProfile';
import Header from './components/Header';

const App = () => {
  const [showCall, setShowCall] = useState(false);
  const [showProfile, setShowProfile] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);

  // Messages state keyed by unique chat IDs (user or group)
  // messages: { chatId: [{ from, text, timestamp, type, file, emoji, audio }, ...] }
  const [messages, setMessages] = useState({});

  const dummyUser = {
    name: 'John Doe',
    email: 'john@example.com',
    avatar: 'https://i.pravatar.cc/150?img=3',
    bio: 'Full-stack developer, tech enthusiast.',
  };

  const mutualGroups = [
    { id: 'g1', name: 'React Developers', members: ['Alice', 'Bob', 'Charlie'] },
    { id: 'g2', name: 'Open Source Contributors', members: ['Diana', 'John', 'Eve'] },
  ];

  // Handle selection from Sidebar (user or group)
  const handleSelect = (item) => {
    setSelectedItem(item);
    // Show profile only if it's a user chat
    if (item.type === 'user') setShowProfile(true);
    else setShowProfile(false);
  };

  // General message sending handler
  const handleSendMessage = (chatId, message) => {
    if (!chatId || !message) return;

    // Ensure message has timestamp for ordering
    if (!message.timestamp) {
      message.timestamp = new Date().toISOString();
    }

    setMessages((prev) => ({
      ...prev,
      [chatId]: prev[chatId] ? [...prev[chatId], message] : [message],
    }));
  };

  // Back from chat to sidebar list
  const handleBack = () => {
    setSelectedItem(null);
    setShowProfile(false);
  };

  return (
    <div className="flex h-screen overflow-hidden bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500">
      {/* Sidebar triggers selection */}
      <Sidebar onSelect={handleSelect} onShowProfile={() => setShowProfile(true)} />

      {/* Main chat and header section */}
      <div className="flex flex-col flex-1">
        <Header onCall={() => setShowCall(true)} onProfile={() => setShowProfile(true)} />

        {selectedItem ? (
          selectedItem.type === 'group' ? (
            <GroupChat
              group={selectedItem.data}
              messages={messages[selectedItem.data.id] || []}
              onSendMessage={(msg) => handleSendMessage(selectedItem.data.id, msg)}
              onBack={handleBack}
            />
          ) : (
            <ChatWindow
              selectedItem={selectedItem}
              messages={messages[selectedItem.data.id] || []}
              onSendMessage={(text) =>
                handleSendMessage(selectedItem.data.id, { from: 'me', text, type: 'text' })
              }
              onBack={handleBack}
            />
          )
        ) : (
          <div className="flex flex-col items-center justify-center flex-1 text-white text-xl font-semibold">
            Select a chat to start messaging
          </div>
        )}
      </div>

      {/* Call UI Overlay */}
      {showCall && <CallWindow type="audio" onEndCall={() => setShowCall(false)} />}

      {/* Profile side panel */}
      {showProfile && selectedItem && selectedItem.type === 'user' && (
        <UserProfile
          user={{ ...dummyUser, ...selectedItem.data }}
          mutualGroups={mutualGroups}
          onClose={() => setShowProfile(false)}
        />
      )}
    </div>
  );
};

export default App;
