import React, { useState, useEffect } from 'react';

const UserProfile = ({ user, onClose, onBlockChange, isBlocked }) => {
  const [blocked, setBlocked] = useState(isBlocked);

  useEffect(() => {
    setBlocked(isBlocked);
  }, [isBlocked]);

  const toggleBlock = () => {
    const confirmMsg = blocked ? `Unblock ${user.name}?` : `Block ${user.name}?`;
    if (window.confirm(confirmMsg)) {
      setBlocked(!blocked);
      onBlockChange(user.id, !blocked);
      onClose();
    }
  };

  const handleReport = () => {
    if (window.confirm(`Report ${user.name}?`)) {
      alert(`${user.name} has been reported.`);
      onClose();
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-70 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg shadow-lg max-w-md w-full p-6 relative">
        <button
          onClick={onClose}
          className="absolute top-4 right-4 text-gray-500 hover:text-gray-800 text-xl font-bold"
        >
          &times;
        </button>

        <div className="flex flex-col items-center">
          <img
            src={user.avatar || 'https://via.placeholder.com/96'}
            alt={`${user.name} profile`}
            className="w-24 h-24 rounded-full object-cover mb-4"
          />
          <h2 className="text-2xl font-semibold mb-1">{user.name}</h2>
          <p className="text-gray-600 mb-2">{user.email}</p>
          {user.bio && <p className="text-center text-gray-700 mb-4 px-4">{user.bio}</p>}
          {blocked && <p className="text-red-600 font-semibold mb-4">User is blocked.</p>}

          <div className="flex gap-4 w-full justify-center mt-4">
            <button
              onClick={toggleBlock}
              className={`font-semibold py-2 px-6 rounded transition ${
                blocked ? 'bg-green-600 hover:bg-green-700' : 'bg-red-600 hover:bg-red-700'
              } text-white`}
            >
              {blocked ? 'Unblock' : 'Block'}
            </button>
            {!blocked && (
              <button
                onClick={handleReport}
                className="bg-yellow-500 hover:bg-yellow-600 text-white font-semibold py-2 px-6 rounded transition"
              >
                Report
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
