import React, { useState } from 'react';

const users = [
  { id: 1, name: 'Alice Johnson', lastMessage: 'Hey there!', lastSeen: '2m ago' },
  { id: 2, name: 'Bob Smith', lastMessage: 'Let’s catch up tomorrow.', lastSeen: '10m ago' },
  { id: 3, name: 'Charlie Lee', lastMessage: 'Sent the documents.', lastSeen: '30m ago' },
  { id: 4, name: 'Diana Ross', lastMessage: 'Thanks!', lastSeen: '1h ago' },
];

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <>
      {/* Mobile hamburger button */}
      <button
        onClick={() => setIsOpen(true)}
        className="fixed top-4 left-4 z-50 md:hidden p-2 rounded-md bg-white dark:bg-gray-800 shadow-lg"
        aria-label="Open sidebar"
      >
        <svg
          className="w-6 h-6 text-gray-800 dark:text-gray-200"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
        </svg>
      </button>

      {/* Overlay for mobile sidebar */}
      <div
        className={`fixed inset-0 bg-black bg-opacity-50 z-40 md:hidden transition-opacity duration-300 ${
          isOpen ? 'opacity-100 pointer-events-auto' : 'opacity-0 pointer-events-none'
        }`}
        onClick={() => setIsOpen(false)}
        aria-hidden="true"
      ></div>

      {/* Sidebar */}
      <aside
        className={`
          fixed top-0 left-0 h-full w-72 bg-white dark:bg-gray-900 shadow-lg z-50
          transform transition-transform duration-300 ease-in-out
          md:static md:translate-x-0
          ${isOpen ? 'translate-x-0' : '-translate-x-full'}
        `}
      >
        {/* Header */}
        <div className="flex items-center justify-between px-6 py-4 border-b border-gray-200 dark:border-gray-700">
          <h1 className="text-2xl font-bold text-gray-900 dark:text-white">Mengobrol</h1>
          {/* Close button for mobile */}
          <button
            onClick={() => setIsOpen(false)}
            className="md:hidden p-2 rounded-md hover:bg-gray-100 dark:hover:bg-gray-800"
            aria-label="Close sidebar"
          >
            <svg
              className="w-6 h-6 text-gray-600 dark:text-gray-300"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        {/* Search input */}
        <div className="p-4">
          <input
            type="text"
            placeholder="Search messages"
            className="w-full px-3 py-2 rounded-md border border-gray-300 dark:border-gray-700 bg-gray-50 dark:bg-gray-800 text-gray-900 dark:text-gray-100 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
        </div>

        {/* User list */}
        <nav className="flex-1 overflow-y-auto px-4 space-y-3">
          {users.map(({ id, name, lastMessage, lastSeen }) => (
            <div
              key={id}
              className="flex items-center space-x-4 p-3 rounded-lg cursor-pointer hover:bg-gray-100 dark:hover:bg-gray-800"
            >
              <div className="w-12 h-12 rounded-full bg-indigo-500 flex items-center justify-center text-white font-bold text-lg">
                {name.charAt(0)}
              </div>
              <div className="flex-1 min-w-0">
                <h3 className="text-gray-900 dark:text-white font-semibold truncate">{name}</h3>
                <p className="text-sm text-gray-500 dark:text-gray-400 truncate">{lastMessage}</p>
              </div>
              <div className="text-xs text-gray-400 dark:text-gray-500 whitespace-nowrap">{lastSeen}</div>
            </div>
          ))}
        </nav>
      </aside>
    </>
  );
};

export default Sidebar;
