import React, { useState } from 'react';
import { FaPaperPlane } from 'react-icons/fa';
import Message from './Message';

const ChatWindow = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const sendMessage = () => {
    if (!input.trim()) return;
    setMessages([...messages, { text: input, sender: 'You', time: new Date() }]);
    setInput('');
  };

  return (
    <div className="flex flex-col flex-1 bg-gray-100 h-screen md:h-auto">
      {/* Header */}
      <div className="p-4 bg-white border-b flex items-center justify-between">
        <h2 className="text-lg font-bold">John Doe</h2>
        <span className="text-sm text-green-500">Online</span>
      </div>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-2">
        {messages.map((msg, index) => (
          <Message key={index} {...msg} />
        ))}
      </div>

      {/* Input */}
      <div className="flex items-center p-4 bg-white border-t">
        <input
          type="text"
          className="flex-1 p-2 rounded-lg border outline-none"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && sendMessage()}
        />
        <button onClick={sendMessage} className="ml-2 p-2 text-white bg-indigo-500 rounded-lg">
          <FaPaperPlane />
        </button>
      </div>
    </div>
  );
};

export default ChatWindow;
