import React, { useState, useEffect, useRef } from 'react';

const ChatWindow = ({ selectedItem, messages, onSendMessage }) => {
  const [newMessage, setNewMessage] = useState('');
  const messagesEndRef = useRef(null);

  useEffect(() => {
    // Scroll to bottom on new messages
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [messages]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;
    onSendMessage(newMessage.trim());
    setNewMessage('');
  };

  if (!selectedItem) {
    return (
      <div className="flex-1 flex items-center justify-center text-gray-400">
        Select a user or group to start chatting.
      </div>
    );
  }

  const { type, data } = selectedItem;

  return (
    <div className="flex flex-col flex-1 bg-white rounded-lg shadow-lg p-6">
      <h2 className="text-xl font-bold mb-4">
        {type === 'user' ? `Chat with ${data.name}` : `Group: ${data.name}`}
      </h2>

      {/* Message List */}
    <div
  className="flex flex-col overflow-y-auto mb-4"
  style={{ maxHeight: 'calc(100vh - 250px)', justifyContent: 'flex-end' }}
>
  {messages.length === 0 && (
    <p className="text-gray-400 text-center mt-20">No messages yet. Say hello!</p>
  )}

  {messages.map((msg, idx) => (
    <div
      key={idx}
      className={`mb-3 max-w-xs p-2 rounded ${
        msg.from === 'me' ? 'bg-indigo-200' : 'bg-gray-200'
      }`}
      style={{ alignSelf: msg.from === 'me' ? 'flex-end' : 'flex-start' }}
    >
      <p>{msg.text}</p>
      <small className="text-gray-500 text-xs">
        {new Date(msg.timestamp).toLocaleTimeString()}
      </small>
    </div>
  ))}
  <div ref={messagesEndRef} />
</div>
      {/* Input Box */}
      <form onSubmit={handleSubmit} className="flex gap-2">
        <input
          type="text"
          placeholder="Type your message..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          className="flex-1 px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-indigo-500"
        />
        <button
          type="submit"
          className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700"
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default ChatWindow;
