import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import ChatWindow from './components/ChatWindow';
import CallWindow from './components/CallWindow';
import UserProfile from './components/UserProfile';
import Header from './components/Header';

const App = () => {
  const [showCall, setShowCall] = useState(false);
  const [showProfile, setShowProfile] = useState(false);
  const [currentChat, setCurrentChat] = useState(null); // { type: 'user'|'group', data: {...} }

  const dummyUser = {
    name: 'John Doe',
    email: 'john@example.com',
    avatar: 'https://i.pravatar.cc/150?img=3',
  };

  return (
    <div className="flex h-screen overflow-hidden bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500">
      <Sidebar onShowProfile={() => setShowProfile(true)} onSelect={setCurrentChat} />
      <div className="flex flex-col flex-1">
        <Header
          onCall={() => setShowCall(true)}
          onProfile={() => setShowProfile(true)}
        />
        <ChatWindow currentChat={currentChat} />
      </div>

      {/* Popup Overlays */}
      {showCall && <CallWindow type="video" onEndCall={() => setShowCall(false)} />}
      {showProfile && <UserProfile user={dummyUser} onClose={() => setShowProfile(false)} />}
    </div>
  );
};

export default App;
