import React, { useState, useRef, useEffect } from 'react';

const initialMessages = [
  { id: 1, sender: 'Alice', text: 'Hey, how are you?', timestamp: '10:00 AM' },
  { id: 2, sender: 'You', text: 'I’m good, thanks! And you?', timestamp: '10:01 AM' },
  { id: 3, sender: 'Alice', text: 'Doing great, thanks!', timestamp: '10:02 AM' },
];

const ChatWindow = () => {
  const [messages, setMessages] = useState(initialMessages);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  // Scroll to bottom on new message
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const sendMessage = () => {
    if (!input.trim()) return;

    const newMessage = {
      id: Date.now(),
      sender: 'You',
      text: input.trim(),
      timestamp: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
    };

    setMessages([...messages, newMessage]);
    setInput('');
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  return (
    <div className="flex flex-col h-full bg-white dark:bg-gray-800 rounded-lg shadow-lg">
      {/* Header */}
      <header className="flex items-center justify-between px-6 py-4 border-b border-gray-200 dark:border-gray-700">
        <h2 className="text-xl font-semibold text-gray-900 dark:text-white">Alice Johnson</h2>
        {/* Placeholder for call/video buttons or settings */}
      </header>

      {/* Messages */}
      <div className="flex-1 overflow-y-auto px-6 py-4 space-y-4">
        {messages.map(({ id, sender, text, timestamp }) => (
          <div
            key={id}
            className={`max-w-xs md:max-w-md px-4 py-2 rounded-lg ${
              sender === 'You' ? 'bg-indigo-500 text-white self-end' : 'bg-gray-200 dark:bg-gray-700 text-gray-900 dark:text-gray-200 self-start'
            }`}
          >
            <p className="whitespace-pre-wrap">{text}</p>
            <span className="block text-xs mt-1 opacity-70">{timestamp}</span>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div className="px-6 py-4 border-t border-gray-200 dark:border-gray-700">
        <textarea
          rows={1}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
          placeholder="Type your message..."
          className="w-full resize-none rounded-md border border-gray-300 dark:border-gray-600 px-4 py-2 text-gray-900 dark:text-white bg-gray-50 dark:bg-gray-700 focus:outline-none focus:ring-2 focus:ring-indigo-500"
        />
        <button
          onClick={sendMessage}
          className="mt-2 px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed"
          disabled={!input.trim()}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatWindow;
